```js:.eslintrc.js
module.exports = {
    //...
    settings: {
        'import/resolver': {
            alias: {
                map: [
                    ['~', './src'],
                ],
                extensions: ['.js', '.jsx', '.ts', '.tsx', '.svelte'],
            },
        },
    },
    rules: {
        // https://github.com/import-js/eslint-plugin-import/blob/main/docs/rules/order.md
        'import/order': [
