const App = () => {
  const [searchValue, setSearchValue] = useState('');

  const searchHandler = useCallback(
    sdebounce((e) => {
      setSearchValue(e.target.value);
    }, 300),
    [],
  );

  return (
    <div>
      <div>{searchValue}</div>
      <input type="text" onChange={searchHandler} />
